import{_ as r,C as a,c as n,o as d,j as o,ag as c,a as t,G as l}from"./chunks/framework.CcXyZPkh.js";const T=JSON.parse('{"title":"Tuples - Tutorial 1","description":"","frontmatter":{"title":"Tuples - Tutorial 1","outline":"deep"},"headers":[],"relativePath":"tutorials/chapter-tuples-tutorial-1.md","filePath":"tutorials/chapter-tuples-tutorial-1.md"}'),s={name:"tutorials/chapter-tuples-tutorial-1.md"},u={id:"exercise-1-scenario-based-problems",tabindex:"-1"};function p(h,e,g,m,q,f){const i=a("Badge");return d(),n("div",null,[e[4]||(e[4]=o("h1",{id:"tuples-tutorial-1",tabindex:"-1"},[t("Tuples - Tutorial 1 "),o("a",{class:"header-anchor",href:"#tuples-tutorial-1","aria-label":'Permalink to "Tuples - Tutorial 1"'},"​")],-1)),o("h2",u,[e[0]||(e[0]=o("strong",null,"Exercise 1: Scenario-Based Problems",-1)),e[1]||(e[1]=t()),l(i,{type:"tip",text:"Question"}),e[2]||(e[2]=t()),e[3]||(e[3]=o("a",{class:"header-anchor",href:"#exercise-1-scenario-based-problems","aria-label":'Permalink to "**Exercise 1: Scenario-Based Problems** <Badge type="tip" text="Question" />"'},"​",-1))]),e[5]||(e[5]=c('<p><strong>Read each scenario carefully. Write function(s) to solve the problem using tuples.</strong></p><hr><h3 id="scenario-1-coordinate-distance-calculator" tabindex="-1"><strong>Scenario 1: Coordinate Distance Calculator</strong> <a class="header-anchor" href="#scenario-1-coordinate-distance-calculator" aria-label="Permalink to &quot;**Scenario 1: Coordinate Distance Calculator**&quot;">​</a></h3><p>A mapping app uses coordinate tuples <code>(x, y)</code> to represent points.</p><p><strong>Given:</strong></p><ul><li>Point A: <code>(0, 0)</code></li><li>Point B: <code>(3, 4)</code></li></ul><p><strong>Task:</strong> Write a function that takes two coordinate tuples and returns the distance between them.</p><p><strong>Test with:</strong></p><ul><li><code>(0, 0)</code> to <code>(3, 4)</code> → Should return <code>5.0</code></li><li><code>(1, 1)</code> to <code>(4, 5)</code> → Should return <code>5.0</code></li></ul><hr><h3 id="scenario-2-color-warmth-detector" tabindex="-1"><strong>Scenario 2: Color Warmth Detector</strong> <a class="header-anchor" href="#scenario-2-color-warmth-detector" aria-label="Permalink to &quot;**Scenario 2: Color Warmth Detector**&quot;">​</a></h3><p>A graphics app uses color tuples <code>(red, green, blue)</code> where each value is 0-255.</p><p>A color is considered &quot;warm&quot; if red dominates (red &gt; green AND red &gt; blue). A color is &quot;cool&quot; if blue dominates (blue &gt; red AND blue &gt; green). Otherwise it&#39;s &quot;neutral&quot;.</p><p><strong>Given:</strong> <code>(200, 50, 80)</code></p><p><strong>Task:</strong> Write a function that takes a color tuple and returns <code>&quot;Warm&quot;</code>, <code>&quot;Cool&quot;</code>, or <code>&quot;Neutral&quot;</code>.</p><p><strong>Test with:</strong></p><ul><li><code>(200, 50, 80)</code> → Should return <code>&quot;Warm&quot;</code></li><li><code>(50, 50, 200)</code> → Should return <code>&quot;Cool&quot;</code></li><li><code>(100, 100, 100)</code> → Should return <code>&quot;Neutral&quot;</code></li></ul><hr><h3 id="scenario-3-midpoint-finder" tabindex="-1"><strong>Scenario 3: Midpoint Finder</strong> <a class="header-anchor" href="#scenario-3-midpoint-finder" aria-label="Permalink to &quot;**Scenario 3: Midpoint Finder**&quot;">​</a></h3><p>A mapping app uses coordinate tuples <code>(x, y)</code>.</p><p>Given two endpoints of a line segment, find the midpoint.</p><p><strong>Given:</strong></p><ul><li>Point A: <code>(2, 4)</code></li><li>Point B: <code>(8, 10)</code></li></ul><p><strong>Task:</strong> Write a function that takes two coordinate tuples and returns a new tuple representing the midpoint.</p><p><strong>Test with:</strong></p><ul><li><code>(2, 4)</code> and <code>(8, 10)</code> → Should return <code>(5.0, 7.0)</code></li><li><code>(0, 0)</code> and <code>(10, 10)</code> → Should return <code>(5.0, 5.0)</code></li></ul><hr><h3 id="scenario-4-rectangle-fit-checker" tabindex="-1"><strong>Scenario 4: Rectangle Fit Checker</strong> <a class="header-anchor" href="#scenario-4-rectangle-fit-checker" aria-label="Permalink to &quot;**Scenario 4: Rectangle Fit Checker**&quot;">​</a></h3><p>A shipping system represents boxes as tuples <code>(width, height)</code>.</p><p>To ship a package, it must fit inside the shipping box. The package CAN be rotated 90 degrees to fit.</p><p><strong>Given:</strong></p><ul><li>Shipping box: <code>(10, 6)</code></li><li>Package: <code>(5, 8)</code></li></ul><p>Normal orientation: Package 5×8 does NOT fit in 10×6 (8 &gt; 6)</p><p>Rotated orientation: Package 8×5 DOES fit in 10×6 (8 ≤ 10 AND 5 ≤ 6)</p><p><strong>Task:</strong> Write a function that takes two tuples (box, package) and returns <code>True</code> if the package fits in either orientation, <code>False</code> otherwise.</p><p><strong>Test with:</strong></p><ul><li>Box <code>(10, 6)</code>, Package <code>(5, 8)</code> → Should return <code>True</code> (fits when rotated)</li><li>Box <code>(10, 6)</code>, Package <code>(7, 7)</code> → Should return <code>False</code> (7 &gt; 6, doesn&#39;t fit either way)</li></ul><hr><h3 id="scenario-5-time-overlap-detector" tabindex="-1"><strong>Scenario 5: Time Overlap Detector</strong> <a class="header-anchor" href="#scenario-5-time-overlap-detector" aria-label="Permalink to &quot;**Scenario 5: Time Overlap Detector**&quot;">​</a></h3><p>A meeting scheduler uses time tuples <code>(hour, minute)</code> in 24-hour format.</p><p>Two meetings overlap if one starts before the other ends AND ends after the other starts.</p><p><strong>Given:</strong></p><ul><li>Meeting A: Start <code>(9, 0)</code>, End <code>(10, 30)</code></li><li>Meeting B: Start <code>(10, 0)</code>, End <code>(11, 0)</code></li></ul><p><strong>Task:</strong> Write a function that takes four time tuples (meeting_a_start, meeting_a_end, meeting_b_start, meeting_b_end) and returns <code>True</code> if they overlap, <code>False</code> otherwise.</p><p><strong>Test with:</strong></p><ul><li>A: <code>(9, 0)</code> to <code>(10, 30)</code>, B: <code>(10, 0)</code> to <code>(11, 0)</code> → Should return <code>True</code> (overlap 10:00-10:30)</li><li>A: <code>(9, 0)</code> to <code>(9, 30)</code>, B: <code>(10, 0)</code> to <code>(11, 0)</code> → Should return <code>False</code> (no overlap)</li></ul><hr><h3 id="scenario-6-triangle-area-from-vertices" tabindex="-1"><strong>Scenario 6: Triangle Area from Vertices</strong> <a class="header-anchor" href="#scenario-6-triangle-area-from-vertices" aria-label="Permalink to &quot;**Scenario 6: Triangle Area from Vertices**&quot;">​</a></h3><p>A geometry app calculates the area of triangles using vertex coordinates. Each vertex is represented as a tuple <code>(x, y)</code>.</p><p><strong>Given:</strong></p><ul><li>Vertex A: <code>(0, 0)</code></li><li>Vertex B: <code>(4, 0)</code></li><li>Vertex C: <code>(2, 3)</code></li></ul><p><strong>Task:</strong> Write a function that takes three coordinate tuples and returns the area of the triangle.</p><p><strong>Test with:</strong></p><ul><li>Vertices <code>(0, 0)</code>, <code>(4, 0)</code>, <code>(2, 3)</code> → Should return <code>6.0</code></li><li>Vertices <code>(0, 0)</code>, <code>(6, 0)</code>, <code>(3, 4)</code> → Should return <code>12.0</code></li></ul><hr><h3 id="scenario-7-quadrant-detector" tabindex="-1"><strong>Scenario 7: Quadrant Detector</strong> <a class="header-anchor" href="#scenario-7-quadrant-detector" aria-label="Permalink to &quot;**Scenario 7: Quadrant Detector**&quot;">​</a></h3><p>A coordinate system uses tuples <code>(x, y)</code> to represent points. The coordinate plane is divided into 4 quadrants:</p><ul><li>Quadrant 1: x &gt; 0 and y &gt; 0</li><li>Quadrant 2: x &lt; 0 and y &gt; 0</li><li>Quadrant 3: x &lt; 0 and y &lt; 0</li><li>Quadrant 4: x &gt; 0 and y &lt; 0</li></ul><p>If a point lies on an axis (x = 0 or y = 0), return <code>0</code>.</p><p><strong>Given:</strong> <code>(5, -3)</code></p><p><strong>Task:</strong> Write a function that takes a coordinate tuple and returns which quadrant (1, 2, 3, or 4) or <code>0</code> if on an axis.</p><p><strong>Test with:</strong></p><ul><li><code>(5, -3)</code> → Should return <code>4</code></li><li><code>(-2, 5)</code> → Should return <code>2</code></li><li><code>(0, 5)</code> → Should return <code>0</code></li></ul><hr><h3 id="scenario-8-point-inside-rectangle-checker" tabindex="-1"><strong>Scenario 8: Point Inside Rectangle Checker</strong> <a class="header-anchor" href="#scenario-8-point-inside-rectangle-checker" aria-label="Permalink to &quot;**Scenario 8: Point Inside Rectangle Checker**&quot;">​</a></h3><p>A graphics system defines rectangles using a corner tuple <code>(left, bottom)</code>, width, and height.</p><p><strong>Given:</strong></p><ul><li>Corner: <code>(1, 1)</code></li><li>Width: <code>4</code></li><li>Height: <code>3</code></li><li>Point: <code>(3, 2)</code></li></ul><p>The rectangle spans from (1, 1) to (5, 4).</p><p><strong>Task:</strong> Write a function that takes a corner tuple, width, height, and a point tuple. Returns <code>True</code> if point is inside (inclusive), <code>False</code> otherwise.</p><p><strong>Test with:</strong></p><ul><li>Corner <code>(1, 1)</code>, Width <code>4</code>, Height <code>3</code>, Point <code>(3, 2)</code> → Should return <code>True</code></li><li>Corner <code>(1, 1)</code>, Width <code>4</code>, Height <code>3</code>, Point <code>(6, 2)</code> → Should return <code>False</code></li><li>Corner <code>(0, 0)</code>, Width <code>10</code>, Height <code>10</code>, Point <code>(10, 10)</code> → Should return <code>True</code> (on edge)</li></ul><hr><h3 id="scenario-9-auction-bid-validator" tabindex="-1"><strong>Scenario 9: Auction Bid Validator</strong> <a class="header-anchor" href="#scenario-9-auction-bid-validator" aria-label="Permalink to &quot;**Scenario 9: Auction Bid Validator**&quot;">​</a></h3><p>An auction system validates incoming bids.</p><p><strong>Parameters:</strong></p><ul><li><code>bids</code>: A list of bid amounts</li><li><code>min_bid</code>: The minimum acceptable bid amount</li></ul><p><strong>Rules:</strong></p><ul><li>Remove all bids that are below the minimum bid amount</li><li>Find the highest valid bid remaining</li><li>Find the original position of that winning bid</li></ul><p><strong>Return:</strong> A tuple <code>(winning_bid, original_position)</code>. If no valid bids remain, return <code>(0, -1)</code>.</p><p><strong>Test with:</strong></p><ul><li>Bids <code>[500, 300, 480, 550, 200, 600]</code>, Min <code>400</code> → <code>(600, 5)</code></li><li>Bids <code>[100, 200, 150]</code>, Min <code>300</code> → <code>(0, -1)</code></li></ul><hr><h3 id="scenario-10-ticket-queue-resolver" tabindex="-1"><strong>Scenario 10: Ticket Queue Resolver</strong> <a class="header-anchor" href="#scenario-10-ticket-queue-resolver" aria-label="Permalink to &quot;**Scenario 10: Ticket Queue Resolver**&quot;">​</a></h3><p>A ticketing system needs to track positions after sorting.</p><p><strong>Parameters:</strong></p><ul><li><code>queue</code>: A list of ticket IDs (strings)</li><li><code>ticket_id</code>: The ticket to find</li></ul><p><strong>Rules:</strong></p><ul><li>Sort the queue alphabetically</li><li>Find the new position of the given ticket after sorting</li></ul><p><strong>Return:</strong> The new position (index) of the ticket. Return <code>-1</code> if not found.</p><p><strong>Test with:</strong></p><ul><li>Queue <code>[&quot;VIP-1&quot;, &quot;REG-5&quot;, &quot;VIP-2&quot;, &quot;REG-3&quot;]</code>, Ticket <code>&quot;REG-5&quot;</code> → <code>1</code></li><li>Queue <code>[&quot;VIP-1&quot;, &quot;REG-5&quot;]</code>, Ticket <code>&quot;UNKNOWN&quot;</code> → <code>-1</code></li></ul><hr><h3 id="scenario-11-anomaly-isolation" tabindex="-1"><strong>Scenario 11: Anomaly Isolation</strong> <a class="header-anchor" href="#scenario-11-anomaly-isolation" aria-label="Permalink to &quot;**Scenario 11: Anomaly Isolation**&quot;">​</a></h3><p>A sensor system needs to detect and remove anomalies from readings.</p><p><strong>Parameters:</strong></p><ul><li><code>readings</code>: A list of sensor values</li><li><code>lower_bound</code>: Minimum valid value</li><li><code>upper_bound</code>: Maximum valid value</li></ul><p><strong>Rules:</strong></p><ul><li>Values outside the range are anomalies</li><li>Record the position of each anomaly</li><li>Remove all anomalies from the list</li><li>Sort the remaining clean data and calculate the median</li></ul><p><strong>Return:</strong> A tuple <code>(list_of_anomaly_positions, median_of_clean_data)</code>. If no clean data remains, median is <code>0</code>.</p><p><strong>Test with:</strong></p><ul><li>Readings <code>[98, 102, 99, 250, 101, 100, 300, 97]</code>, Range <code>90-110</code> → <code>([3, 6], 99.5)</code></li></ul><hr><h3 id="scenario-12-roster-conflict-resolver" tabindex="-1"><strong>Scenario 12: Roster Conflict Resolver</strong> <a class="header-anchor" href="#scenario-12-roster-conflict-resolver" aria-label="Permalink to &quot;**Scenario 12: Roster Conflict Resolver**&quot;">​</a></h3><p>A tournament system needs to resolve player conflicts between two teams.</p><p><strong>Parameters:</strong></p><ul><li><code>team_a</code>: A list of player names</li><li><code>team_b</code>: A list of player names</li></ul><p><strong>Rules:</strong></p><ul><li>Find players appearing in BOTH lists (conflicts)</li><li>Remove conflicts from BOTH lists</li><li>Sort both remaining lists alphabetically</li><li>Determine which team has more players left</li></ul><p><strong>Return:</strong> A tuple <code>(winner, team_a_remaining, team_b_remaining)</code> where winner is <code>&quot;Team A&quot;</code>, <code>&quot;Team B&quot;</code>, or <code>&quot;Tie&quot;</code>.</p><p><strong>Test with:</strong></p><ul><li>Team A <code>[&quot;Ali&quot;, &quot;Sara&quot;, &quot;Ahmad&quot;, &quot;Mei&quot;]</code>, Team B <code>[&quot;Sara&quot;, &quot;John&quot;, &quot;Ahmad&quot;, &quot;Lee&quot;]</code> → <code>(&quot;Tie&quot;, [&quot;Ali&quot;, &quot;Mei&quot;], [&quot;John&quot;, &quot;Lee&quot;])</code></li></ul>',112))])}const k=r(s,[["render",p]]);export{T as __pageData,k as default};
